}

/* DARK MODE DEFAULT - Premium Sports Feel */
:root {
  --background: 222 47% 11%; /* Deep Navy */
  --foreground: 210 40% 98%;

  --card: 223 47% 14%; /* Slightly lighter navy */
  --card-foreground: 210 40% 98%;

  --popover: 223 47% 14%;
